mapper.select:
select cart_id, amount 
from suho_cart 
where user_id=#{user_id} and goods_id=#{goods_id}

mapper.update:
update suho_cart
set amount = amount + #{amount}
where cart_id=#{cart_id}

mapper.select:
select nvl(sum(price*amount), 0) 
from suho_cart c, suho_goods g
where c.goods_id=g.goods_id and user_id=#{user_id}
