mapper.select:
SELECT * FROM (
SELECT ROWNUM AS rnum, a.*
FROM (
SELECT *
FROM donation_transaction order by donation_date desc
) a
WHERE ROWNUM
<= #{end}
)
WHERE rnum >= #{start}

SELECT * FROM ( SELECT ROWNUM AS rnum, dt.transaction_id, dt.content_id, c.title AS content_title, dt.donor_id, dt.amount, dt.donation_date, dt.payment_method, dt.status, dt.merchant_uid, dt.refund_amount, dt.refund_date, dt.refund_reason, dt.reward_id, r.name AS reward_name FROM donation_transaction dt JOIN suho_donation_content c ON dt.content_id = c.content_id LEFT JOIN suho_reward r ON dt.reward_id = r.reward_id
dt.donor_id = #{keyword}
AND c.title LIKE '%' || #{keyword} || '%'
ORDER BY dt.donation_date DESC ) WHERE rnum BETWEEN #{start} AND #{end}

SELECT COUNT(*) FROM donation_transaction dt JOIN suho_donation_content c ON dt.content_id = c.content_id LEFT JOIN suho_reward r ON dt.reward_id = r.reward_id
AND dt.donor_id = #{keyword}
AND c.title LIKE CONCAT('%', #{keyword}, '%')
