mapper.select:
SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (
        SELECT * FROM suho_goods ORDER BY goods_id DESC
    ) a
    WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}

mapper.update:
update suho_goods
set location = #{location}, name = #{name}, price = #{price}, description = #{description}, img = #{img}, detail_img = #{detail_img}
where goods_id = #{goods_id}
