mapper.select:
SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (
        SELECT * FROM suho_user ORDER BY name DESC
    ) a
    WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}

mapper.update:
update suho_user
set passwd = MYPACK.ENCRYPT(#{passwd}), name=#{name}, address1=#{address1}, address2=#{address2}, gender=#{gender}, birth=#{birth}
where user_id=#{user_id}
